import React, { Component } from 'react';
import Title from "./components/Title"
import Form from "./components/Form"
import Weather from "./components/Weather"

const API_KEY="c397546bedbab8c7f0889eced4686172"


class App extends Component {
  getWeather=async(e)=>{
    e.preventDefault();
    const city=e.target.elements.name.city.value;
    const country=e.target.elements.name.country.value;
    const api_call = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=London,uk&appid=${API_KEY}&units=metric`)
    const data= await api_call.json();
    console.log(data);
  }
  render() {
     return(
       <div>
        <Title/>
        <Form getWeather={this.getWeather}/>
        <Weather/>
       </div>
     );
  }
}

export default App;
